import logo from './logo.svg';
import React, { useState } from 'react';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import SvgIcon from '@mui/material/SvgIcon';


// Icons.
import SmallImageIcon from '@mui/icons-material/PhotoSizeSelectSmall';
import MediumImageIcon from '@mui/icons-material/PhotoSizeSelectLarge';
import LargeImageIcon from '@mui/icons-material/PhotoSizeSelectActual';
import TemplateIcon from '@mui/icons-material/Wallpaper';
import CustomImageIcon from '@mui/icons-material/ImageAspectRatio';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

import './App.css';

 // Available size buttons.

  const buttons = [
    {
      value: 'small',
      label: 'Small',
      width: 512,
      height: null,
      icon: <SmallImageIcon/>,
    },
    {
      value: 'medium',
      label: 'Medium',
      width: 1024,
      height: null,
      icon: <MediumImageIcon/>,
    },
    {
      value: 'large',
      label: 'Large',
      width: 2048,
      height: null,
      icon: <LargeImageIcon/>,
    },

  ];


function App() {

  const [fileChosen, setFileChosen] = useState(false);

  const fileUpload = (e) =>{
      setFileChosen(true);
  }


  return (
    <div className="App">
      <header className="App-header">
         <Button
              variant="contained"
              component="label"
            >
              {fileChosen ? "File Chosen" : "Upload File"}
              <input
                type="file"
                id="file"
                onChange={fileUpload}
                hidden
              />
            </Button>
      </header>
    </div>
  );
}

export default App;
