import logo from './logo.svg';
import React, { useState } from 'react';





import './App.css';



 // Available size buttons.

const buttons = [
  {
    value: 'small',
    label: 'Small',
    width: 512,
    height: null,
    icon: "",
  },
  {
    value: 'medium',
    label: 'Medium',
    width: 1024,
    height: null,
    icon: "",
  },
  {
    value: 'large',
    label: 'Large',
    width: 2048,
    height: null,
    icon: "",
  },

];


function App() {




  const [fileChosen, setFileChosen] = useState(false);
  const [image, setImage] = useState('');
  const [version, setVersion] = useState('');
  const [preset, setPreset] = useState('small');
  const [width, setWidth] = useState('500');
  const [height, setHeight] = useState('500');
  const [fit, setFit] = useState('cover');
  const [gravity, setGravity] = useState('center');
  const [type, setType] = useState('image/jpeg');
  const [quality, setQuality] = useState(100);
  const [background, setBackground] = useState('');
  const [showAdvanced, setShowAdvanced] = useState(false);

function fileUpload(e){
     let imgInput = document.getElementById('imageInput');
      if(e.target.files) {
        let imageFile = e.target.files[0]; //here we get the image file
        var reader = new FileReader();
        reader.readAsDataURL(imageFile);
        reader.onloadend = function (e) {
          var myImage = new Image(); // Creates image object
          myImage.src = e.target.result; // Assigns converted image to image object
          myImage.onload = function(ev) {
            var myCanvas = document.getElementById("canvas"); // Creates a canvas object
            var myContext = myCanvas.getContext("2d"); // Creates a contect object
            myCanvas.width = myImage.width; // Assigns image's width to canvas
            myCanvas.height = myImage.height; // Assigns image's height to canvas
            myContext.drawImage(myImage,0,0); // Draws the image on canvas
            let imgData = myCanvas.toDataURL("image/jpeg",0.75); // Assigns image base64 string in jpeg format to a variable
          }
        }
      }
  }

function resizeImage(e){
  console.log(e.currentTarget.size)
}

  return (
   <div
    style={{textAlign:'center'}}
   >
    <div
    style={{padding:'25px'}}
    >

        {fileChosen ? "File Chosen" : "Upload File"}
        <input
          type="file"
          id="file"
          onChange={fileUpload}

        />

      </div>
    <div container spacing={1} style={{textAlign:"center"}}>
      {
        buttons.map(obj => (
          <div key={obj.value} style={{marginBottom:'15px'}}>
            <button
              variant="outlined"
              fullWidth
              onClick={resizeImage}
              size={obj.width}
            >
              <div>
                <div className="iconContainer">{ obj.icon }</div>
                <div>{ obj.label }</div>
                <div variant="caption" component="div">{ `${obj.width || 'auto'} × ${obj.height || 'auto'}` }</div>
              </div>
            </button>
          </div>
        ))
      }
    </div>
    <div
     style={{padding:'25px'}}
    >
      <button
          variant="contained"
          component="label"
      >Download</button>
    </div>
    <canvas id="canvas"></canvas>

  </div>
  );
}

export default App;
