import logo from './logo.svg';
import React, { useState } from 'react';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import SvgIcon from '@mui/material/SvgIcon';


// Icons.
import SmallImageIcon from '@mui/icons-material/PhotoSizeSelectSmall';
import MediumImageIcon from '@mui/icons-material/PhotoSizeSelectLarge';
import LargeImageIcon from '@mui/icons-material/PhotoSizeSelectActual';
import TemplateIcon from '@mui/icons-material/Wallpaper';
import CustomImageIcon from '@mui/icons-material/ImageAspectRatio';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

import './App.css';

 // Available size buttons.

  const buttons = [
    {
      value: 'small',
      label: 'Small',
      width: 512,
      height: null,
      icon: <SmallImageIcon/>,
    },
    {
      value: 'medium',
      label: 'Medium',
      width: 1024,
      height: null,
      icon: <MediumImageIcon/>,
    },
    {
      value: 'large',
      label: 'Large',
      width: 2048,
      height: null,
      icon: <LargeImageIcon/>,
    },

  ];


function App() {

  const [fileChosen, setFileChosen] = useState(false);

  const fileUpload = (e) =>{
      setFileChosen(true);
  }


  return (
    <div className="App">
      <header className="App-header">
         <Button
              variant="contained"
              component="label"
            >
              {fileChosen ? "File Chosen" : "Upload File"}
              <input
                type="file"
                id="file"
                onChange={fileUpload}
                hidden
              />
            </Button>
              <Grid container spacing={1}>
          {
            buttons.map(obj => (
              <Grid key={obj.value} item xs={4}>
                <Button
                  className={classes.sizeButton}
                  variant="outlined"
                  color={preset === obj.value ? 'secondary' : 'default'}
                  onClick={() => setPreset(obj.value)}
                  disabled={disabled}
                  fullWidth
                  {...rest}
                >
                  <div>
                    <div className="iconContainer">{ obj.icon }</div>
                    <div>{ obj.label }</div>
                    <Typography variant="caption" component="div">{ `${obj.width || 'auto'} Ã— ${obj.height || 'auto'}` }</Typography>
                  </div>
                </Button>
              </Grid>
            ))
          }
          <Grid item xs={4}>
            {
              preset === 'template' &&
              <TemplateSelector
                value={template}
                options={templateOptions.items}
                onChange={handleChangeTemplate}
                variant="outlined"
                color={mainColor}
                size="small"
                disabled={disabled}
                fullWidth
              />
            }
            {
              preset === 'custom' &&
              <>
                <TextField
                  className={classes.sizeTextField}
                  label="Width"
                  placeholder="auto"
                  value={width}
                  onChange={handleChangeWidth}
                  color={mainColor}
                  variant="outlined"
                  size="small"
                  type="number"
                  disabled={disabled}
                  InputLabelProps={{ shrink: true }}
                  inputProps={{ min: 0, max: MAX_DIMENSION }}
                  fullWidth
                />
                <TextField
                  label="Height"
                  placeholder="auto"
                  value={height}
                  onChange={handleChangeHeight}
                  color={mainColor}
                  variant="outlined"
                  size="small"
                  type="number"
                  disabled={disabled}
                  InputLabelProps={{ shrink: true }}
                  inputProps={{ min: 0, max: MAX_DIMENSION }}
                  fullWidth
                />
              </>
            }
          </Grid>
        </Grid>
      </header>
    </div>
  );
}

export default App;
