import logo from './logo.svg';
import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { Button } from '@mui/material';

import SvgIcon from '@mui/material/SvgIcon';
import { makeStyles } from '@mui/styles';
import Typography from '@mui/material/Typography';
// Icons.
import SmallImageIcon from '@mui/icons-material/PhotoSizeSelectSmall';
import MediumImageIcon from '@mui/icons-material/PhotoSizeSelectLarge';
import LargeImageIcon from '@mui/icons-material/PhotoSizeSelectActual';
import TemplateIcon from '@mui/icons-material/Wallpaper';
import CustomImageIcon from '@mui/icons-material/ImageAspectRatio';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

import './App.css';



 // Available size buttons.

const buttons = [
  {
    value: 'small',
    label: 'Small',
    width: 512,
    height: null,
    icon: <SmallImageIcon/>,
  },
  {
    value: 'medium',
    label: 'Medium',
    width: 1024,
    height: null,
    icon: <MediumImageIcon/>,
  },
  {
    value: 'large',
    label: 'Large',
    width: 2048,
    height: null,
    icon: <LargeImageIcon/>,
  },

];


function App() {

  const useStyles = makeStyles(theme => ({
    titleContainer: {
      '& > .MuiTypography-root': {
        display: 'flex',
        alignItems: 'center',
      }
    },

    versionSelect: {
      flexGrow: 1,
    },
    sizeButton: {
      height: 128,
      textTransform: 'none',
      transition: 'none',  // Stops the borderWidth transition.
      '& .iconContainer': {
        lineHeight: 0,
      },
      '& .MuiSvgIcon-root': {
        fontSize: 48,
        opacity: 0.2,
      },
      // "outlinedSecondary" is treated as if the button is selected.
      '&.MuiButton-outlinedSecondary': {
        borderWidth: 3,
      },
      '&.MuiButton-outlinedSecondary .MuiSvgIcon-root': {
        opacity: 0.4,
      },
    },

    advancedButtonContainer: {
      display: 'flex',
      alignItems: 'center',
    },
    dividerContainer: {
      flexGrow: 1,
    },

    fitIconContainer: {
      textAlign: 'center',
      lineHeight: 0,
      '& > .MuiSvgIcon-root': {
        fontSize: '2rem',
      },
    },
  }));

  const classes = useStyles();

  const [fileChosen, setFileChosen] = useState(false);
  const [image, setImage] = useState('');
  const [version, setVersion] = useState('');
  const [preset, setPreset] = useState('small');
  const [width, setWidth] = useState('500');
  const [height, setHeight] = useState('500');
  const [fit, setFit] = useState('cover');
  const [gravity, setGravity] = useState('center');
  const [type, setType] = useState('image/jpeg');
  const [quality, setQuality] = useState(100);
  const [background, setBackground] = useState('');
  const [showAdvanced, setShowAdvanced] = useState(false);

function fileUpload(e){
     let imgInput = document.getElementById('imageInput');
      if(e.currentTarget.files) {
        let imageFile = e.target.files[0]; //here we get the image file
        var reader = new FileReader();
        reader.readAsDataURL(imageFile);
        reader.onloadend = function (e) {
          var myImage = new Image(); // Creates image object
          myImage.src = e.target.result; // Assigns converted image to image object
          myImage.onload = function(ev) {
            var myCanvas = document.getElementById("myCanvas"); // Creates a canvas object
            var myContext = myCanvas.getContext("2d"); // Creates a contect object
            myCanvas.width = myImage.width; // Assigns image's width to canvas
            myCanvas.height = myImage.height; // Assigns image's height to canvas
            myContext.drawImage(myImage,0,0); // Draws the image on canvas
            let imgData = myCanvas.toDataURL("image/jpeg",0.75); // Assigns image base64 string in jpeg format to a variable
          }
        }
      }
  }



  return (
   <Box
    sx={{textAlign:'center'}}
   >
    <Box
    sx={{padding:'25px'}}
    >
      <Button
        variant="contained"
        component="label"
        >
        {fileChosen ? "File Chosen" : "Upload File"}
        <input
          type="file"
          id="file"
          onChange={fileUpload}
          hidden
        />
      </Button>
      </Box>
    <Box container spacing={1} sx={{textAlign:"center"}}>
      {
        buttons.map(obj => (
          <Box key={obj.value} item xs={10} sx={{marginBottom:'15px'}}>
            <Button
              variant="outlined"
              fullWidth

            >
              <div>
                <div className="iconContainer">{ obj.icon }</div>
                <div>{ obj.label }</div>
                <Typography variant="caption" component="div">{ `${obj.width || 'auto'} × ${obj.height || 'auto'}` }</Typography>
              </div>
            </Button>
          </Box>
        ))
      }
    </Box>
    <Box
     sx={{padding:'25px'}}
    >
      <Button
          variant="contained"
          component="label"
      >Download</Button>
    </Box>
    <canvas id="canvas"></canvas>

  </Box>
  );
}

export default App;
