import logo from './logo.svg';
import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

import './App.css';




function App() {

  const [fileChosen, setFileChosen] = useState(false);

  const fileUpload = (e) =>{
      setFileChosen(true);
  }


  return (
    <div className="App">
      <header className="App-header">
         <Button
              variant="contained"
              component="label"
            >
              {fileChosen ? "File Chosen" : "Upload File"}
              <input
                type="file"
                id="file"
                onChange={fileUpload}
                hidden
              />
            </Button>
      </header>
    </div>
  );
}

export default App;
