import logo from './logo.svg';
import React, { useState } from 'react';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import SvgIcon from '@mui/material/SvgIcon';
import { makeStyles } from '@mui/material/styles';

// Icons.
import SmallImageIcon from '@mui/icons-material/PhotoSizeSelectSmall';
import MediumImageIcon from '@mui/icons-material/PhotoSizeSelectLarge';
import LargeImageIcon from '@mui/icons-material/PhotoSizeSelectActual';
import TemplateIcon from '@mui/icons-material/Wallpaper';
import CustomImageIcon from '@mui/icons-material/ImageAspectRatio';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

import './App.css';



 // Available size buttons.

const buttons = [
  {
    value: 'small',
    label: 'Small',
    width: 512,
    height: null,
    icon: <SmallImageIcon/>,
  },
  {
    value: 'medium',
    label: 'Medium',
    width: 1024,
    height: null,
    icon: <MediumImageIcon/>,
  },
  {
    value: 'large',
    label: 'Large',
    width: 2048,
    height: null,
    icon: <LargeImageIcon/>,
  },

];


function App() {

const classes = useStyles();
  // Styles.
const useStyles = makeStyles(theme => ({
  titleContainer: {
    '& > .MuiTypography-root': {
      display: 'flex',
      alignItems: 'center',
    }
  },
  titleText: {
    marginRight: theme.spacing(2),
  },
  versionSelect: {
    flexGrow: 1,
  },
  sizeButton: {
    height: 128,
    textTransform: 'none',
    transition: 'none',  // Stops the borderWidth transition.
    '& .iconContainer': {
      lineHeight: 0,
      marginBottom: theme.spacing(0.5),
    },
    '& .MuiSvgIcon-root': {
      fontSize: 48,
      opacity: 0.2,
    },
    // "outlinedSecondary" is treated as if the button is selected.
    '&.MuiButton-outlinedSecondary': {
      borderWidth: 3,
    },
    '&.MuiButton-outlinedSecondary .MuiSvgIcon-root': {
      opacity: 0.4,
    },
  },
  sizeTextField: {
    marginBottom: theme.spacing(1),
  },
  advancedButtonContainer: {
    padding: theme.spacing(3, 1, 0, 0),
    display: 'flex',
    alignItems: 'center',
  },
  dividerContainer: {
    flexGrow: 1,
    paddingLeft: theme.spacing(1),
  },
  advancedContainer: {
    paddingTop: theme.spacing(3),
  },
  fitIconContainer: {
    textAlign: 'center',
    color: theme.palette.secondary.main,
    lineHeight: 0,
    '& > .MuiSvgIcon-root': {
      fontSize: '2rem',
    },
  },
}));


  const [fileChosen, setFileChosen] = useState(false);
   const [version, setVersion] = useState('');
  const [preset, setPreset] = useState('small');
  const [width, setWidth] = useState('500');
  const [height, setHeight] = useState('500');
  const [fit, setFit] = useState('cover');
  const [gravity, setGravity] = useState('center');
  const [type, setType] = useState('image/jpeg');
  const [quality, setQuality] = useState(100);
  const [background, setBackground] = useState('');
  const [showAdvanced, setShowAdvanced] = useState(false);

  const fileUpload = (e) =>{
      setFileChosen(true);
  }


  return (
    <div className="App">
      <header className="App-header">
         <Button
              variant="contained"
              component="label"
            >
              {fileChosen ? "File Chosen" : "Upload File"}
              <input
                type="file"
                id="file"
                onChange={fileUpload}
                hidden
              />
            </Button>
              <Grid container spacing={1}>
          {
            buttons.map(obj => (
              <Grid key={obj.value} item xs={4}>
                <Button
                  className={classes.sizeButton}
                  variant="outlined"
                  color={preset === obj.value ? 'secondary' : 'default'}
                  onClick={() => setPreset(obj.value)}
                  disabled={disabled}
                  fullWidth
                  {...rest}
                >
                  <div>
                    <div className="iconContainer">{ obj.icon }</div>
                    <div>{ obj.label }</div>
                    <Typography variant="caption" component="div">{ `${obj.width || 'auto'} Ã— ${obj.height || 'auto'}` }</Typography>
                  </div>
                </Button>
              </Grid>
            ))
          }

        </Grid>
      </header>
    </div>
  );
}

export default App;
