import logo from './logo.svg';
import React, { useState } from 'react';





import './App.css';



 // Available size buttons.

const buttons = [
  {
    value: 'small',
    label: 'Small',
    width: 512,
    height: null,
    icon: "",
  },
  {
    value: 'medium',
    label: 'Medium',
    width: 1024,
    height: null,
    icon: "",
  },
  {
    value: 'large',
    label: 'Large',
    width: 2048,
    height: null,
    icon: "",
  },

];


function App() {




  const [fileChosen, setFileChosen] = useState(false);
  const [imageInfo, setImageInfo] = useState('');
  const [imageType, setImageType] = useState('');
  const [image, setImage] = useState('');
  const [version, setVersion] = useState('');
  const [preset, setPreset] = useState('small');
  const [width, setWidth] = useState('500');
  const [height, setHeight] = useState('500');
  const [fit, setFit] = useState('cover');
  const [gravity, setGravity] = useState('center');
  const [type, setType] = useState('image/jpeg');
  const [quality, setQuality] = useState(100);
  const [background, setBackground] = useState('');
  const [showAdvanced, setShowAdvanced] = useState(false);

function fileUpload(e){
     let imgInput = document.getElementById('imageInput');
      if(e.target.files) {
        let imageFile = e.target.files[0]; //here we get the image file
        let imageFileType = e.target.files[0].type;
        setImageType(imageFileType);
        var reader = new FileReader();
        reader.readAsDataURL(imageFile);
        reader.onloadend = function (e) {
          var myImage = new Image(); // Creates image object
          myImage.src = e.target.result; // Assigns converted image to image object
          document.getElementById('image').src = myImage.src;
          myImage.onload = function(ev) {
            var myCanvas = document.getElementById("canvas"); // Creates a canvas object
            var myContext = myCanvas.getContext("2d"); // Creates a contect object
            myCanvas.width = myImage.width; // Assigns image's width to canvas
            myCanvas.height = myImage.height; // Assigns image's height to canvas
            myContext.drawImage(myImage,0,0); // Draws the image on canvas
            let imgData = myCanvas.toDataURL(imageFileType,0.75); // Assigns image base64 string in jpeg format to a variable
            setImageInfo(imgData);
          }
        }
      }
  }


function determineNewHeight(originalHeight, originalWidth, newWidth){
    return (originalHeight / originalWidth) * newWidth;
}

function resizeImage(e){
 const requestedSize = e.currentTarget.getAttribute('size');
 const refImage = document.getElementById('canvas');
 const ctx = refImage.getContext('2d')
// Using the Image() constructor
let imagePath = document.getElementById('image').src;
const originalImage = new Image();
originalImage.src = imagePath;
originalImage.addEventListener('load', function() {
    const originalWidth = originalImage.naturalWidth;
    const originalHeight = originalImage.naturalHeight;

    const aspectRatio = originalWidth/originalHeight;

    let newWidth = requestedSize;
    let newHeight = newWidth/aspectRatio;
    refImage.width = newWidth;
    refImage.height = newHeight;
    ctx.drawImage(originalImage, 0, 0, newWidth, newHeight);
    let imgData = refImage.toDataURL(imageType,0.75);
    setImageInfo(imgData);

})



}

function changeFileType(e){
    let canvas = document.getElementById('canvas');
    let requestedImageType = e.currentTarget.value;
    let type = "";
    if(requestedImageType == "jpeg"){
      type = "image/jpeg";
    }
    else if(requestedImageType == "png"){
      type = "image/png";
    }
    else if(requestedImageType == "webp"){
      type = "image/webp";
    }

    let imgData = canvas.toDataURL(type,0.75);

    setImageInfo(imgData);

} 

function downloadImage(){
    let fileName = document.getElementById('file').files[0].name;
    let tempLink = document.createElement('a');
    tempLink.download = fileName;
    tempLink.href = imageInfo;
    console.log(tempLink.href);
    tempLink.click();
}

  return (
   <div
    style={{textAlign:'center'}}
   >
   <div className="logo-wrapper" style={{textAlign:'left',padding:'5px 20px'}}>
    <svg style={{width:'100px'}} id="Layer_1" data-name="Layer 1" width={186} height={66} xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" viewBox="0 0 185.82 65.98"><defs><style dangerouslySetInnerHTML={{__html: ".cls-1{fill:url(#linear-gradient);}" }} /><linearGradient id="linear-gradient" x1={0} x2={0} y1={0} y2="65.98" gradientUnits="userSpaceOnUse"><stop offset={0} stopColor="#2846ff" /><stop offset={1} stopColor="#1e28aa" /></linearGradient></defs><title>_HIVO LOGO</title><path className="cls-1" d="M169.21,64.53a39.07,39.07,0,0,1-21.09,0,22.67,22.67,0,0,1-8.46-5q-8.15-7.25-8.15-26.71,0-21.9,11.22-29Q148.81,0,158.66,0q15.48,0,21.91,9.77,5.25,8,5.25,23.08,0,19.47-8.15,26.71A22.75,22.75,0,0,1,169.21,64.53ZM151.47,13.94q-2.49,1.44-3.85,5.93T146.26,33q0,8.64,1.41,13.17c.93,3,2.23,5,3.89,5.93a14.73,14.73,0,0,0,7.1,1.4A14,14,0,0,0,165.72,52q5.34-3.07,5.34-19,0-16.11-5.16-19.1a14.71,14.71,0,0,0-7.24-1.45A14.45,14.45,0,0,0,151.47,13.94Z" /><path className="cls-1" d="M126.18,1.18H112s.27,27.06-2.88,34.25l-7,16.07h-.55l-7-16.07C91.33,28.24,91.6,1.18,91.6,1.18H77.39s-.26,27.15,2.88,34.34S93,64.81,93,64.81h17.56s9.59-22.11,12.74-29.29S126.18,1.18,126.18,1.18Z" /><path className="cls-1" d="M0,64.8V1.18H14.21l-.27,25.25h21L34.67,1.18H48.88V64.8H34.67l.27-26.24h-21l.27,26.24Z" /><path className="cls-1" d="M70,29.51l.27,35.29H56l.36-29.32L56,1.18h14.3Z" /></svg>
    </div>
    <div className="info" style={{textAlign:'center'}}>
      <p>Upload an image and resize or convert below.</p>
    </div>
    <div
    style={{padding:'25px'}}
    >

        {fileChosen ? "File Chosen" : "Upload File"}
        <input
          type="file"
          id="file"
          onChange={fileUpload}
          accept="image/png, image/jpeg, image/webp"
        />

      </div>
    <div container spacing={1} style={{textAlign:"center"}}>
      {
        buttons.map(obj => (
          <div key={obj.value} style={{marginBottom:'15px'}}>
            <button
              variant="outlined"
              fullwidth="true"
              onClick={resizeImage}
              size={obj.width}
            >
              <div>
                <div className="iconContainer">{ obj.icon }</div>
                <div>{ obj.label }</div>
                <div variant="caption" component="div">{ `${obj.width || 'auto'} × ${obj.height || 'auto'}` }</div>
              </div>
            </button>
          </div>
        ))
      }
    </div>
    <div className="convert-box">
      <select name="convert" id="convert" onChange={changeFileType}>
        <option value="">Convert Image</option>
        <option value="jpeg">jpeg</option>
        <option value="png">png</option>
        <option value="webp">webp</option>
      </select>
    </div>
    <div
     style={{padding:'25px'}}
    >
      <button
          variant="contained"
          component="label"
          onClick={downloadImage}
      >Download</button>
    </div>
    <div className="blub-info">
    <p>Need more features? visit <a href="https://hivo.com.au" style={{color:'blue',fontWeight:'bold',textDecoration:'none'}}>hivo.com.au</a></p>
    </div>
    <canvas id="canvas" style={{display:'none'}}></canvas>
    <img src="" id="image" style={{display:'none'}}/>
  </div>
  );
}

export default App;
